import React  from 'react';
import { motion } from 'framer-motion';
import { useTranslation } from 'react-i18next'; //for lan added
import Popup from "./popup"
import { useState, useEffect } from 'react';
import translationEN from "../locale/en.json"  ///
import translationAR from "../locale/ar.json" ///
import Button from '@material-ui/core/Button';







export const Service = (props) => {
  const [changeStatus] = React.useState(false);
  
  const [ t, i18n ] =useTranslation(); //for lan added


  const [open, setOpen] = React.useState(false);

  // const classes = useStyles();


  const handleClickOpen = () => {
    setOpen(true);
  };

  // const handleClose = () => {
  //   setOpen(false);
  // };

  // // const handlePopup = () => {
  // //   setDataImg(Popup)
  // // };



  const [dataImg, setDataImg] = useState({}) ///
  useEffect(() => {
    if(i18n.language === 'en')
      return;
        setDataImg(translationAR) 
  }, [i18n.language])
  useEffect(() => {
    if(i18n.language === 'ar') 
      return;
        setDataImg(translationEN) 
  }, [i18n.language])




  return (
    <div id='service' className='text-center'  style={{ borderRadius:"5px",marginBottom:"5px" }}>
      <div className='container'>
        <div className='section-title'>
          <h2>{t('titles.service')}</h2>
        
        </div>
        <div className='row'>
          {props.data ///
            ? props.data.map((d, i) => ( ///
                <motion.div  key={`${d.name}-${i}`} className='col-md-4'>
                  {' '}
                  <i className={d.icon}></i>
                  <div className='service-desc'>
                    <h3>{d.name}</h3>
                    <motion.p hidden={changeStatus}>{d.text}</motion.p>
                    {/* <div className="details-button">
                    <button className={d.details} onClick="?" >More Details</button>
                    </div> */}
                          <div className="popup">

                          {/* <Button variant="contained" style={{ background:"white",color:"black",fontSize:"1.3vw",fontWeight:500,textTransform:"none" }}
                          onClick={handleClickOpen}>
                            {d.img}     
                          </Button> */}
                          <Popup />


                              {/* <button variant="contained" style={{ background:"white",color:"black",fontSize:"1.3vw",fontWeight:500,textTransform:"none" }} sonClick={handleClickOpen} onClick= ""  >
                                    {d.img}
                              </button> */}

                            {/* <Popup data={dataImg.popups} />  */}
                          </div>
                  </div>
                </motion.div>
              ))
            : 'loading'}
        </div>
      </div>
    </div>
  )

}
