import React  from 'react';
import { motion } from 'framer-motion';
import { useTranslation } from 'react-i18next'; //for lan added
// import Popup from "./popup"
import { useState, useEffect } from 'react';
// import translationEN from "../locale/en.json"  ///
// import translationAR from "../locale/ar.json" ///
import data from "./data";
import Model from "./Model";
import './Home.css';
import{FontAwesomeIcon} from '@fortawesome/react-fontawesome';










export const Service = (props) => {
  const [changeStatus] = React.useState(false);
  
  const [ t ] =useTranslation(); //for lan added

  // const [dataImg, setDataImg] = useState({}) ///
  // useEffect(() => {
  //   if(i18n.language === 'en')
  //     return;
  //       setDataImg(translationAR) 
  // }, [i18n.language])
  // useEffect(() => {
  //   if(i18n.language === 'ar') 
  //     return;
  //       setDataImg(translationEN) 
  // }, [i18n.language])

  const [model, setModel] = useState(false);
	const [tempdata, setTempdata] = useState([]);

	const getData = (img, title, desc1 , desc2 , desc3) => {
		let tempData = [img, title, desc1, desc2 , desc3];
		setTempdata((item) => [1, ...tempData]);

		return setModel(true);
	};

  




  return (
    <div id='service' className='text-center'  style={{ borderRadius:"5px",marginBottom:"5px" }}>
      <div className='container'>
        <div className='section-title'>
          <h2>{t('titles.service')}</h2>
        </div>

        <div className='row'>
          {props.data ///
            ? props.data.map((d, i) => ( ///
                <motion.div  key={`${d.name}-${i}`} className='col-md-4'>
                  {' '}
                  <i className={d.icon}></i>
                  <div className='service-desc'>
                    <h3>{d.name}</h3>
                    <motion.p hidden={changeStatus}>{d.text}</motion.p>

                              <div className="popup">
                                              <div
                                              className=" col-md-2 col-lg-4 mx-1 mb-4 "
                                              key={index}
                                              >
                                                  <FontAwesomeIcon icon={item.icon} className="icon   justify-content-center"  style={{marginLeft:"191px" , marginTop:"30px" , color:"white" }}/> 
                                                    <button
                                                      className="btn btn-light" 
                                                      onClick={() =>
                                                        getData(item.imgSrc,item.title, item.desc1, item.desc2,item.desc3)
                                                      }
                                                    >
                                                      {" "}
                                                      Click me{" "}
                                                    </button>
                                              </div>
                                    
                              </div>
                  </div>
                </motion.div>
              ))
            : 'loading'}
        </div>
      </div>
      {model === true ? (
				<Model
					img={tempdata[1]}
					title={tempdata[2]}
					desc1={tempdata[3]}
                    desc2={tempdata[4]}
                    desc3={tempdata[5]}
					hide={() => setModel(false)}
				/>
			) : (
				" "
			)}
    </div>
  )

}
