import { useState, useEffect } from 'react'
import { Navigation } from './components/navigation'
import { Header } from './components/header'
import { About } from './components/about'
import { Partners } from './components/partners'
import {  Services } from './components/services'
// import { Serv } from './components/serv'
import { Contact } from './components/contact'
import  i18n  from './i18n'  ///
import translationEN from "./locale/en.json"  ///
import translationAR from "./locale/ar.json" ///
import { useTranslation } from 'react-i18next'; //for lan added
import SmoothScroll from 'smooth-scroll'
import { Industries } from './components/industries'
import i18next from 'i18next'


export const scroll = new SmoothScroll('a[href*="#"]', {
  speed: 1000,
  speedAsDuration: true,
})




const App = () => {



  const [landingPageData, setLandingPageData] = useState({}) ///


  useEffect(() => {
    if(i18n.language == 'en')   setLandingPageData(translationEN) 
  }, [])
  useEffect(() => {
    if(i18n.language == 'ar')   setLandingPageData(translationAR) 
  }, [])



  return (
    <div>
      <Navigation />
      <Header data={landingPageData.Header} />
      <Services data={landingPageData.Services} />  {/* /// */}
      {/* <Serv data={landingPageData.Serv}/> */}
      <Industries data={landingPageData.Industries}/>

       <Partners /> 

      <About data={landingPageData.About} />
      <Contact data={landingPageData.Contact} />   
      </div>
    )




}

export default App


