import React, { useState } from "react";
import data from "./data";
import Model from "./Model";
import './Home.css';
import{FontAwesomeIcon} from '@fortawesome/react-fontawesome';
// import "../node_modules/bootstrap/dist/css/bootstrap.min.css";




const Home = () => {
	const [model, setModel] = useState(false);
	const [tempdata, setTempdata] = useState([]);

	const getData = (img, title, desc1 , desc2 , desc3) => {
		let tempData = [img, title, desc1, desc2 , desc3];
		setTempdata((item) => [1, ...tempData]);

		return setModel(true);
	};

	return (
		<>
			{/* <h1>Home </h1> */}

			<section  id="home" className="py-4 py-lg container rounded  " >
				<h1 className="text-center text-info mt-3">services</h1>
				<div className="row justify-content-center align-item-center    " >
				{/* border border-white */}
					{data.cardData.map((item, index) => {
						return (
							<div
								className=" col-md-2 col-lg-4 mx-1 mb-4 "
								key={index}
							>
								<div className="card p-0 overflow-hidden h-100 shadow "  >
									{/* <img
										src={item.imgSrc}
										className="card-img-top"
										alt=""
									/> */}

                                       <div icon={item.icon} className="icon   justify-content-center"  style={{marginLeft:"191px" , marginTop:"30px" , color:"white" }}/> 
									<div className="card-body">
										<h5 className="card-title">{item.title}</h5>
										<p className="card-text"></p>
										<button
											className="btn btn-light" 
											onClick={() =>
												getData(item.imgSrc,item.title, item.desc1, item.desc2,item.desc3)
											}
										>
											{" "}
											Click me{" "}
										</button>
									</div>
								</div>
							</div>
						);
					})}
				</div>
			</section> 
			{model === true ? (
				<Model
					img={tempdata[1]}
					title={tempdata[2]}
					desc1={tempdata[3]}
                    desc2={tempdata[4]}
                    desc3={tempdata[5]}
					hide={() => setModel(false)}
				/>
			) : (
				" "
			)}
		</>
	);
};

export default Home;
